import PropTypes from 'prop-types';
import React, { useState } from 'react';
import { Row, Col, Card,Button, Alert } from 'react-bootstrap';

import * as Yup from 'yup';
import { Formik } from 'formik';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios'; // Import axios for making HTTP requests

const ResetPassword1 = ({ className, onLogin, ...rest }) => {
  const navigate = useNavigate(); // Initialize useNavigate
  const [error, setError] = useState(null); // State to store authentication error

  return (
    <React.Fragment>
      <Formik
        initialValues={{
          email: ''

        }}
        validationSchema={Yup.object().shape({
          username: Yup.string().required('email is required')

        })}
        onSubmit={(values, { setSubmitting }) => {
          // Send login request to backend server
          axios.post('http://localhost:5000/Login', values)
            .then(response => {
              // If login is successful, call the onLogin callback with username and password
              onLogin(values);
              // If login is successful, navigate to the dashboard
              navigate('/app/process/default');
            })
            .catch(error => {
              // If there's an error with authentication, display the error message
              setError(error.response.data.error);
            })
            .finally(() => {
              setSubmitting(false);
            });
        }}
      >
        {({ errors, handleBlur, handleChange, handleSubmit, isSubmitting, touched, values }) => (
          <form noValidate onSubmit={handleSubmit} className={className} {...rest}>




            <div className="auth-wrapper">
        <div className="auth-content">
          <div >
            <span className="r" />
            <span className="r s" />
            <span className="r s" />
            <span className="r" />
          </div>
          <Card className="borderless">
            <Row className="align-items-center text-center">
              <Col>
                <Card.Body className="card-body">
                  <div className="mb-4">
                    <i className="feather icon-mail auth-icon" />
                  </div>
                  <h3 className="mb-3 f-w-400">Reset Password</h3>
                  <div className="input-group mb-4">
                  <input
                className="form-control"
                name="username"
                placeholder='Email'
                onBlur={handleBlur}
                onChange={handleChange}
                type="text"
                value={values.username}
              />
              {touched.username && errors.username && <small className="text-danger form-text">{errors.username}</small>}
                  </div>
                  <button className="btn btn-primary mb-4" disabled={isSubmitting} size="large" type="submit" variant="primary">Reset password</button>
                  {/* <Button className="btn-block" disabled={isSubmitting} size="large" type="submit" variant="primary"></Button> */}
                </Card.Body>
              </Col>
            </Row>
          </Card>
        </div>
      </div>



















            {error && (
              <Col sm={12}>
                <Alert variant="danger">{error}</Alert>
              </Col>
            )}




          </form>
        )}
      </Formik>

      <hr />
    </React.Fragment>
  );
};

ResetPassword1.propTypes = {
  className: PropTypes.string,
  onLogin: PropTypes.func.isRequired
};

export default ResetPassword1;
