import PropTypes from 'prop-types';
import React, { useState } from 'react';
import { Row, Col, Card, Button, Alert } from 'react-bootstrap';
import * as Yup from 'yup';
import { Formik } from 'formik';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios'; // Import axios for making HTTP requests

const ResetPassword1 = ({ className, ...rest }) => {
  const navigate = useNavigate(); // Initialize useNavigate
  const [error, setError] = useState(null); // State to store authentication error
  const [success, setSuccess] = useState(null); // State to store success message

  return (
    <React.Fragment>
      <Formik
        initialValues={{
          email: ''
        }}
        validationSchema={Yup.object().shape({
          email: Yup.string().email('Invalid email').required('Email is required')
        })}
        onSubmit={(values, { setSubmitting }) => {
          // Send reset password request to backend server
          axios.post('http://localhost:5000/resetpassword', values)
            .then(response => {
              // If password reset is successful, display success message
              setSuccess('Password successfully reset. Please log in with your new password.');
              setError(null);
              // Optionally, navigate to the login page after reset
              setTimeout(() => navigate('/login'), 3000); // Redirect to login page after 3 seconds
            })
            .catch(error => {
              // If there's an error, display the error message
              setError(error.response?.data?.error || 'An error occurred');
              setSuccess(null);
            })
            .finally(() => {
              setSubmitting(false);
            });
        }}
      >
        {({ errors, handleBlur, handleChange, handleSubmit, isSubmitting, touched, values }) => (
          <form noValidate onSubmit={handleSubmit} className={className} {...rest}>
            <div className="auth-wrapper">
              <div className="auth-content">
                <Card className="borderless">
                  <Row className="align-items-center text-center">
                    <Col>
                      <Card.Body className="card-body">
                        <div className="mb-4">
                          <i className="feather icon-mail auth-icon" />
                        </div>
                        <h3 className="mb-3 f-w-400">Reset Password</h3>
                        <div className="input-group mb-4">
                          <input
                            className="form-control"
                            name="email"
                            placeholder="Email"
                            onBlur={handleBlur}
                            onChange={handleChange}
                            type="text"
                            value={values.email}
                          />
                          {touched.email && errors.email && (
                            <small className="text-danger form-text">{errors.email}</small>
                          )}
                        </div>
                        <button
                          className="btn btn-primary mb-4"
                          disabled={isSubmitting}
                          type="submit"
                          variant="primary"
                        >
                          Reset Password
                        </button>
                      </Card.Body>
                    </Col>
                  </Row>
                </Card>
              </div>
            </div>

            {error && (
              <Col sm={12}>
                <Alert variant="danger">{error}</Alert>
              </Col>
            )}

            {success && (
              <Col sm={12}>
                <Alert variant="success">{success}</Alert>
              </Col>
            )}
          </form>
        )}
      </Formik>

      <hr />
    </React.Fragment>
  );
};

ResetPassword1.propTypes = {
  className: PropTypes.string,
};

export default ResetPassword1;
